import React, { useEffect, useState } from "react";
import "./GoogleAuth.css";

const GoogleAuth = () => {
  const [isSignedIn = null, setIsSignedIn] = useState(0);

  //Hook equivalent to: ComponentDidMount and ComponentDidUpdate
  useEffect(() => {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "16710659472-hh6hjs4bhftik940isu6t7nfl9q79lp9.apps.googleusercontent.com",
          scope: "email",
          plugin_name: "Stream platform",
        })
        .then(() => {
          console.log("Victorius");
          const auth = window.gapi.auth2.getAuthInstance();
          setIsSignedIn(auth.isSignedIn.get());
          auth.isSignedIn.listen(changeLoginState);
        });
    });
  });

  const changeLoginState = () => {
    setIsSignedIn(window.gapi.auth2.getAuthInstance().isSignedIn.get());
  };

  const loginState = () => {
    console.log("Tarzan: " + isSignedIn);

    if (isSignedIn === null) {
      return null;
    }

    if (isSignedIn) {
      return (
        <button
          onClick={() => {
            window.gapi.auth2.getAuthInstance().signOut();
          }}
          className="btn"
        >
          <i class="material-symbols-outlined login">login</i>
          <span className="login-text">Sign out</span>
        </button>
      );
    }

    if (!isSignedIn) {
      return (
        <button
          onClick={() => {
            window.gapi.auth2.getAuthInstance().signIn();
          }}
        >
          Sign in
        </button>
      );
    }
  };

  return <div>{loginState()}</div>;
};

export default GoogleAuth;
