import React, { useEffect, useState } from "react";

const GoogleAuth = () => {
  const [isSignedIn = null, setIsSignedIn] = useState(0);

  //Hook equivalent to: ComponentDidMount and ComponentDidUpdate
  useEffect(() => {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "16710659472-hh6hjs4bhftik940isu6t7nfl9q79lp9.apps.googleusercontent.com",
          scope: "email",
          plugin_name: "Stream platform",
        })
        .then(() => {
          console.log("Victorius");

          setIsSignedIn(window.gapi.auth2.getAuthInstance().isSignedIn.get());
          auth.isSignedIn.listen(changeLoginState);
        });
    });
  });

  const changeLoginState = () => {
    setIsSignedIn(window.gapi.auth2.getAuthInstance().isSignedIn.get());
  };

  const loginState = () => {
    console.log("Tarzan: " + isSignedIn);

    if (isSignedIn === null) {
      return <div>Not sure if loged</div>;
    }

    if (isSignedIn) {
      return <div>Loged</div>;
    }

    if (!isSignedIn) {
      return <div>Not loged</div>;
    }
  };

  return <div>{loginState()}</div>;
};

export default GoogleAuth;
